import React, { useState } from "react";

const Carousel = ({ projects }) => {
  const [currentP, setCurrentP] = useState(0);

  const prevP = () => {
    setCurrentP((prevP) => (prevP === 0 ? projects.length - 1 : prevP - 1));
  };

  const nextP = () => {
    setCurrentP((prevP) => (prevP === projects.length - 1 ? 0 : prevP + 1));
  };

  return (
    <div className="relative w-full max-w-3xl mx-auto mt-10">
      <div className="overflow-hidden relative">
        <div
          className="flex transition-transform duration-500"
          style={{ transform: `translateX(-${currentP * 100}%)` }}
        >
          {projects.map((project, index) => (
            <div className="min-w-full" key={index}>
              <img
                src={project}
                className="w-full"
                alt={`Slide ${index + 1}`}
              />
            </div>
          ))}
        </div>
      </div>
      <button
        className="absolute top-1/2 transform -translate-y-1/2 left-4 bg-white p-2 rounded-full shadow"
        onClick={prevP}
      >
        &#10094;
      </button>
      <button
        className="absolute top-1/2 transform -translate-y-1/2 right-4 bg-white p-2 rounded-full shadow"
        onClick={nextP}
      >
        &#10095;
      </button>
    </div>
  );
};

export default Carousel;
