import React, { useState, useEffect } from "react";
import "./Slider.css"; // Optional: for styling

const Slider = ({ slides }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  useEffect(() => {
    const interval = setInterval(nextSlide, 3000); // Change slide every 3 seconds
    return () => clearInterval(interval); // Clear interval on component unmount
  }, [slides.length]); // Depend on slides.length to reset interval if slides change

  return (
    <div className="slider-container">
      <button onClick={prevSlide} className="nav-button">
        Previous
      </button>
      <div className="slide-wrapper">
        <div
          className="slides"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {slides.map((slide, index) => (
            <div key={index} className="slide">
              {slide}
            </div>
          ))}
        </div>
      </div>
      <button onClick={nextSlide} className="nav-button">
        Next
      </button>
    </div>
  );
};

export default Slider;
